<?php$versions = Fwk::getAllApplicationVersions(APPLICATION_NAME);?><table id="appVersions">	<tr>		<th>Version</th>		<th>Date</th>		<th>Description</th>	</tr>	<?php	foreach ($versions as $version) {	?>	<tr>		<td><?php echo $version['version']; ?></td>		<td><?php echo Fwk::parseDate($version['date']); ?></td>		<td><?php echo $version['description']; ?></td>	</tr>	<?php	}	?></table>